import androidx.lifecycle.ViewModel
import androidx.fragment.app.Fragment

interface UserRepository {
    fun addUser(name: String)
    fun getUserCount(): Int
}

class FakeUserRepository : UserRepository {
    private val users = mutableListOf<String>()
    override fun addUser(name: String) { users.add(name) }
    override fun getUserCount(): Int = users.size
}

class FakeViewModel(private val repo: UserRepository) : ViewModel() {
    fun addUser(name: String) = repo.addUser(name)
    fun userCount() = repo.getUserCount()
}

class FakeFragment : Fragment() {
    private val viewModel = FakeViewModel(FakeUserRepository())
    // ...existing code...
}

