// Spy: Records information about calls, can call real methods.
class RealLogger : Logger {
    var lastMessage: String? = null
    override fun log(msg: String) { lastMessage = msg }
}

class SpyTest {
    @Test
    fun testLoggerSpy() {
        val logger = spy(RealLogger())
        logger.log("Hello")
        verify(logger).log("Hello")
        assert(logger.lastMessage == "Hello")
    }
}

